module MaxAnagram7 ( maxAnagram7 )
    where

maxAnagram7 :: Integer -> Integer
maxAnagram7 n = case findMaxAnagram7 n of
                  Just a -> a
                  Nothing -> -1

findMaxAnagram7 :: Integer -> Maybe Integer
